import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';

class Settings extends Component {
    constructor(props) {
        super(props);
        this.state = {
            name: '',
            email: '',
            mobile: '',
            country: '',
            postcode: '',
            password1: '',
            password2: '',
            loading: false,
            confirmMessage: null
        };
        this.submitHandler = this.submitHandler.bind(this);
        this.changeHandler = this.changeHandler.bind(this);
    }

    changeHandler = (event) => {
        this.setState({[event.target.name]: event.target.value});
    }

    submitHandler(event) {
        event.preventDefault();

        const name = this.state.name;
        const email = this.state.email;
        const mobile = this.state.mobile;
        const country = this.state.country;
        const postcode = this.state.postcode;
        const password1 = this.state.password1;
        const password2 = this.state.password2;

        this.setState({loading: true});

        // axios.defaults.headers = {
        //     "Content-Type": "application/json",
        //     Authorization: `Token ${this.props.token}`
        // };

        // axios
        //     .post('http://127.0.0.1:8000/api/', {
        //         name: name,
        //         email: email,
        //         mobile: mobile,
        //         country: country,
        //         postcode: postcode,
        //         password1: password1,
        //         password2: password2,
        //     })
        //     .then(function (response) {
        //         console.log(response);
                    // this.setState({confirmMessage: 'Settings saved'});
                    // this.props.history.push('/account');
        //     })
        //     .catch(function (error) {
        //         console.log(error);
                    // this.setState({confirmMessage: 'Saving failed, please try again'});
        //     });

        this.setState({confirmMessage: 'Settings saved'});

    }   

    render() {
        let confirmMessage = (
            <p>&nbsp;</p>
        );

        if (this.state.confirmMessage !== null) {
            confirmMessage = (
                <p className="confirm-msg">{this.state.confirmMessage}</p>
            );
        }

        return (
            <div className="Home">
                <div class="row">
                    <div class="col">
                        <h1 className="page-title display-4">Add Car</h1>
                    </div>
                </div>
                <div class="row justify-content-md-center mb-4 mt-4">
                    <div class="col-4">
                        <div>{confirmMessage}</div>
                        <form onSubmit={this.submitHandler}>
                            <div class="form-group">
                                <input type="text" placeholder="Make" name="make" value={this.state.make} onChange={this.changeHandler} className="form-control form-control-lg" />
                            </div>
                            <div class="form-group">
                                <input type="text" placeholder="Model" name="model" value={this.state.model} onChange={this.changeHandler} className="form-control form-control-lg" />
                            </div>
                            <div class="form-group">
                                <input type="text" placeholder="Year" name="year" value={this.state.year} onChange={this.changeHandler} className="form-control form-control-lg" />
                            </div>
                            <input type="submit" value="Add Car" className="btn btn-primary btn-lg" />
                        </form>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(Settings);
