import React, { Component } from 'react';
import { connect } from 'react-redux';
import * as actions from '../store/Actions/Auth';
import Spinner from '../components/Spinner/Spinner';

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            password: '',
            error: false,
            errorMessage: ''
        };
        this.submitHandler = this.submitHandler.bind(this);
        this.changeHandler = this.changeHandler.bind(this);
        this.validationHandler = this.validationHandler.bind(this);
    }

    // static getDerivedStateFromProps(nextProps, prevState){
    //     if(nextProps.loading !== prevState.loading){
    //       return { someState: nextProps.someValue};
    //    }
    //    else return null;
    //  }

    changeHandler = (event) => {
        this.setState({[event.target.name]: event.target.value});
    }

    validationHandler(username, password) {
        if (username.length > 0 && password.length > 0) {
            return true;
        } else {
            return false;
        }
    }

    submitHandler(event) {
        event.preventDefault();

        const username = this.state.username;
        const password = this.state.password;

        const validated = this.validationHandler(username, password);

        //this.props.form.validateFields((err, values) => {
            if (this.state.password.length > 0) {
                this.props.onAuth(username, password);
                if(true) {
                    console.log(this.props.error);
                    //this.props.history.push('/account');
                }    
            } else {
                this.state.errorMessage = "Username and password are required";
                this.state.error = true;
            }
        //});

        // axios
        //     .post('http://127.0.0.1:8000/api/', {
        //         'make': data.make,
        //         'model': data.model,
        //     }, {
        //         headers: {
        //             "Authorization": 'AUTHORIZATION_KEY',
        //             "Content-Type": 'application/json'
        //         }
        //     })
        //     .then(res => console.log(res))
        //     .catch(error => console.err(error))
    }

    render() {

        let errorMessage = (
            <p>&nbsp;</p>
        );
        if (this.props.error) {
            errorMessage = (
                <p className="error-msg">{this.props.error.message}</p>
            );
        }

        let validationMessage = '...';
        if (this.state.error) {
            validationMessage = (
                <p className="error-msg">{this.state.errorMessage}</p>
            );
        }

        let btnMessage = "Login";
        if(this.props.loading) {
            btnMessage = "Loading...";
        }

        return (
            <div className="Account">
                <div class="row">
                    <div class="col">
                        <h1 className="page-title display-4">Login</h1>
                    </div>
                </div>
                <div class="row justify-content-md-center mb-4 mt-0">
                    <div class="col-4">
                        <div>{errorMessage}</div>
                        <div>{validationMessage}</div>
                        <form onSubmit={this.submitHandler}>
                            <div class="form-group">
                                <input type="text" placeholder="Username" name="username" value={this.state.username} onChange={this.changeHandler} className="form-control form-control-lg" />
                            </div>
                            <div class="form-group">
                                <input type="password" placeholder="Password" name="password" value={this.state.password} onChange={this.changeHandler} className="form-control form-control-lg" />
                            </div>
                            <input type="submit" value={btnMessage} className="btn btn-primary btn-lg" />
                        </form>
                    </div>
                </div>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        loading: state.loading,
        error: state.error
    }
}

const mapDispatchToProps = dispatch => {
    return {
        onAuth: (username, password) => dispatch(actions.authLogin(username, password)) 
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Login);
