import React, {Component} from 'react'
//import React from 'react';
// import {useHistory} from "react-router-dom";
import { useForm } from "react-hook-form";

class Home extends Component {

    constructor() {
        super()
        this.state = {
          results: []
        }
    }

    onSubmit = (data) => {
        console.log(data);

        // axios
        //     .post('http://127.0.0.1:8000/api/', {
        //         'make': data.make,
        //         'model': data.model,
        //     }, {
        //         headers: {
        //             "Authorization": 'AUTHORIZATION_KEY',
        //             "Content-Type": 'application/json'
        //         }
        //     })
        //     .then(res => console.log(res))
        //     .catch(error => console.err(error))

        history.push({
            pathname: '/results',
            state: { make: data.make, model: data.model }
        });
    }

    // const togglePersonsHandler = (event) => {
    //     const doesShow = this.state.showPersons;
    //     this.setState({
    //       showPersons: !doesShow
    //     });
    //   }

    render() {

        const history = useHistory();
        const {register, handleSubmit, errors} = useForm();

        return (
            <div className="Home">
                <h1>Home</h1>
                <p>Enter your make, and model:</p>
                <form onSubmit={handleSubmit(onSubmit)}>
                <input type='text' placeholder='Make' name='make' ref={register({required: true})} />
                <input type='text' placeholder='Model' name='model' ref={register({required: true})} />
                <input type='submit' />
                {errors.make && <p>Error: Model is required</p>}
                {errors.make && <p>Error: Make is required</p>}
                </form>
            </div>
        )
    }
}
