import React, { Component } from 'react';
import axios from 'axios';
import './App.css';
import Cockpit from '../components/Cockpit/Cockpit';
import Persons from '../components/Persons/Persons';

const list = [
  {
    "id": 1,
    "title": "title 1",
    "body": "body 1"
  },
  {
    "id": 2,
    "title": "title 2",
    "body": "body 2"
  },
  {
    "id": 3,
    "title": "title 3",
    "body": "body 3"
  }
]

class App extends Component {
  state = {
    issues: list,
    persons: [
      { id: 'abc1', name: 'Josh', age: 30 },
      { id: 'abc2', name: 'Joe', age: 29 },
      { id: 'abc3', name: 'Sha', age: 28 }
    ],
    showPersons: false
  }

  nameChangedHandler = (event, id) => {
    // get the index of the person id where the event happened
    const personIndex = this.state.persons.findIndex(p => {
      return p.id === id;
    });
    // get the specific person object and copy it to a new object using the index
    const person = {
      ...this.state.persons[personIndex]
    };
    // update the person's name to the new name value
    person.name = event.target.value;
    // copy the full state into a new array
    const persons = [...this.state.persons];
    // set the person array copy to override the persons state array at this index
    persons[personIndex] = person;
    // set the state for this persons array to be the persons array we created
    this.setState({persons: persons})
  }

  deletePersonHandler = (personIndex) => {
    const persons = [...this.state.persons];
    persons.splice(personIndex, 1);
    this.setState({persons: persons});
  }

  togglePersonsHandler = (event) => {
    const doesShow = this.state.showPersons;
    this.setState({
      showPersons: !doesShow
    });
  }

  render() {
    let persons = null;

    if(this.state.showPersons) {
      persons = <Persons
        persons={this.state.persons}
        clicked={this.deletePersonHandler}
        changed={this.nameChangedHandler} />
    }

    return (
      <div className="App">
        <Cockpit
          title={this.props.appTitle}
          showPersons={this.state.showPersons}
          clicked={this.togglePersonsHandler} />
        {persons}
      </div>
    );
  }
}

export default App;
